package main

import (
	"fmt"
	"strings"
	"time"

	"github.com/brookesargent/advent-of-code/helper"
)

func main() {
	start := time.Now()

	input := ""
	inputSlice := strings.Split(input, "")

	packetMarker := findMarker(inputSlice, 4)
	messageMarker := findMarker(inputSlice, 14)

	fmt.Printf("Answer 1 is: %d\n", packetMarker)
	fmt.Printf("Answer 2 is: %d\n", messageMarker)

	fmt.Println("Program duration: " + time.Since(start).String())
}

func findMarker(input []string, length int) int {
	var marker int
	for i := 0; i < len(input)-(length-1); i++ {
		subset := input[i : i+length]
		deduped := helper.RemoveDuplicateValues(subset)
		if len(deduped) == length {
			marker = i + length
			break
		}
	}
	return marker
}